  #{templateId,jdbcType=INTEGER}, #{deleted,jdbcType=INTEGER}, #{style,jdbcType=LONGVARCHAR}
  )

mapper.update:

update tb_control_component
set component_id = #{componentId,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=VARCHAR},
  place_id = #{placeId,jdbcType=INTEGER},
  template_id = #{templateId,jdbcType=INTEGER}
  deleted = #{deleted,jdbcType=INTEGER},
  `style` = #{style,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.delete:
UPDATE tb_control_component
SET deleted = 1
WHERE place_id=#{placeId}

DELETE FROM tb_control_component
WHERE place_id=#{placeId}
AND template_id=#{templateId}

mapper.select:
  	SELECT * FROM tb_control_component
  	WHERE `type`='button'
  	AND deleted = 0

		AND place_id=#{placeId}


  		AND template_id=#{templateId}


  		AND FIND_IN_SET(component_id, #{componentIds})

