mapper.insert:


  SELECT LAST_INSERT_ID()

insert into tb_gateway_history (uuid, device_id, `date`, 
  battery, `power`, history_type
  )
values (#{uuid,jdbcType=VARCHAR}, #{deviceId,jdbcType=VARCHAR}, #{date,jdbcType=VARCHAR}, 
  #{battery,jdbcType=BIGINT}, #{power,jdbcType=BIGINT}, #{historyType,jdbcType=VARCHAR}
  )

mapper.select:
SELECT * FROM tb_gateway_history


		uuid=#{uuid}


		AND device_id=#{deviceId}



		AND `date`=#{date}


		AND history_type=#{historyType}

SELECT uuid,device_id,SUM(battery) AS battery, SUM(`power`) AS `power` FROM tb_gateway_history WHERE history_type='daily'
AND uuid=#{record.uuid}
AND device_id=#{record.deviceId}
AND DATE(STR_TO_DATE(`date`, "%Y-%m-%d")) >= DATE(#{startDate})
AND DATE(STR_TO_DATE(`date`,"%Y-%m-%d")) <= DATE(#{endDate})
GROUP BY uuid,device_id

  	SELECT date,SUM(battery) AS battery,SUM(`power`) AS `power`
  	FROM tb_gateway_history


  			history_type=#{record.historyType}



			AND uuid=#{record.uuid}


			AND device_id=#{record.deviceId}



			AND DATE(STR_TO_DATE(`date`, "%Y-%m-%d")) >= DATE(#{startDate})



			AND DATE(STR_TO_DATE(`date`,"%Y-%m-%d")) <= DATE(#{endDate})


  	GROUP BY date

mapper.delete:
DELETE FROM tb_gateway_history
WHERE uuid=#{uuid}

	AND id=#{id}
