mapper.insert:


  SELECT LAST_INSERT_ID()

insert into tb_gateway_mode (`name`, command, hex, 
  hex_success, hex_fail, `order`
  )
values (#{name,jdbcType=VARCHAR}, #{command,jdbcType=VARCHAR}, #{hex,jdbcType=VARCHAR}, 
  #{hexSuccess,jdbcType=VARCHAR}, #{hexFail,jdbcType=VARCHAR}, #{order,jdbcType=INTEGER}
  )

mapper.select:
SELECT * FROM tb_gateway_mode


		`name`=#{name}



		AND command=#{command}



		AND `order`=#{order}


LIMIT 1

SELECT COUNT(*) FROM tb_gateway_mode
WHERE
command=#{command}

	AND id != #{id}
