mapper.select:
SELECT COUNT(*) FROM tb_gateway_record
WHERE uuid=#{record.uuid}
AND device_id=#{record.deviceId}

	AND create_time >= #{startTime}


	AND create_time <= #{endTime}

SELECT * FROM tb_gateway_record
WHERE uuid=#{record.uuid}
AND device_id=#{record.deviceId}
ORDER BY create_time DESC
LIMIT 1

  	SELECT uuid,device_id,SUM(battery) AS battery,SUM(power) AS power
  	FROM tb_gateway_record


  			uuid=#{record.uuid}


  			AND device_id=#{record.deviceId}


	  		AND create_time >= #{startTime}


	  		AND create_time <= #{endTime}


  	GROUP BY uuid,device_id

  	SELECT HOUR(create_time) AS `hour`,SUM(battery) AS battery FROM tb_gateway_record


  			uuid=#{record.uuid}


  			AND device_id=#{record.deviceId}


	  		AND create_time >= #{startTime}


	  		AND create_time <= #{endTime}



	  		AND FIND_IN_SET(uuid, #{uuids})


  	GROUP BY `hour`
  	ORDER BY `hour`

  	SELECT SUM(battery) AS battery,SUM(power) AS power
  	FROM tb_gateway_record


  			FIND_IN_SET(uuid, #{uuids})


	  		AND create_time >= #{startTime}


	  		AND create_time <= #{endTime}

mapper.delete:
DELETE FROM tb_gateway_record
WHERE uuid=#{uuid}

	AND id=#{id}
