mapper.insert:


  SELECT LAST_INSERT_ID()

insert into tb_gateway_timing (`name`, uuid, device_ids, 
  weeks, everyday, `date`, 
  `time`, op_code, cron
  )
values (#{name,jdbcType=VARCHAR}, #{uuid,jdbcType=VARCHAR}, #{deviceIds,jdbcType=VARCHAR}, 
  #{weeks,jdbcType=VARCHAR}, #{everyday,jdbcType=INTEGER}, #{date,jdbcType=VARCHAR}, 
  #{time,jdbcType=VARCHAR}, #{opCode,jdbcType=INTEGER}, #{cron,jdbcType=VARCHAR}
  )

mapper.select:
SELECT * FROM tb_gateway_timing
WHERE uuid=#{uuid}

	AND `name` LIKE CONCAT('%', #{search}, '%')
