mapper.update:

update tb_gateway_warning
set uuid = #{uuid,jdbcType=VARCHAR},
  device_id = #{deviceId,jdbcType=VARCHAR},
  warn_type = #{warnType,jdbcType=VARCHAR},
  `revoke` = #{revoke,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, uuid, device_id, warn_type, `revoke`, create_time, update_time, tenant_id
from tb_gateway_warning
where id = #{id,jdbcType=INTEGER}

SELECT * FROM tb_gateway_warning
WHERE uuid=#{record.uuid}
AND device_id=#{record.deviceId}

	AND update_time >= #{startTime}


	AND update_time <= #{endTime}


	AND `revoke`=#{record.revoke}

ORDER BY create_time DESC

mapper.update:
UPDATE tb_gateway_warning SET `revoke`=#{revoke}
WHERE uuid=#{uuid}
AND device_id=#{deviceId}

mapper.select:
SELECT SUM(IF(FIND_IN_SET('overTemperatureAlarm', warn_type),1,0)) AS over_temperature_alarm_count, SUM(IF(FIND_IN_SET('overTemperaturePowerOff', warn_type),1,0)) AS over_temperature_power_off_count, SUM(IF(FIND_IN_SET('overcurrentAlarm', warn_type),1,0)) AS overcurrent_alarm_count, SUM(IF(FIND_IN_SET('overcurrentPowerOff', warn_type),1,0)) AS overcurrent_power_off_count, SUM(IF(FIND_IN_SET('overvoltageAlarm', warn_type),1,0)) AS overvoltage_alarm_count, SUM(IF(FIND_IN_SET('overvoltagePowerOff', warn_type),1,0)) AS overvoltage_power_off_count, SUM(IF(FIND_IN_SET('undervoltageAlarm', warn_type),1,0)) AS undervoltage_alarm_count FROM tb_gateway_warning
uuid=#{uuid}
AND device_id=#{deviceId}
LIMIT 1

mapper.delete:
  	DELETE FROM tb_gateway_warning


	  		uuid=#{uuid}


	  		AND device_id=#{deviceId}
